// Generated by Selenium IDE
const { Builder, By, Key, until } = require("selenium-webdriver");
const assert = require("assert");
const { it } = require("mocha");

let driver;
let vars;
async function signIn() {
  await driver.get("http://localhost:5173");
  await driver.sleep(5000);
  await driver.findElement(By.id("email")).click();
  await driver.findElement(By.id("email")).sendKeys("profiletest@uncw.edu");
  await driver.findElement(By.id("password")).click();
  await driver.findElement(By.id("password")).sendKeys("test1234" + Key.RETURN);
  await driver.sleep(3000);
  await driver.switchTo().alert().accept();
}

// Start of tests
describe("Edit Profile", function () {
  this.timeout(30000);
  beforeEach(async function () {
    driver = await new Builder().forBrowser("chrome").build();
    vars = {};
  });
  afterEach(async function () {
    await driver.quit();
  });
  it("Edit Firstname to new name", async function () {
    await signIn();
    await driver.get("http://localhost:5173/components/Profile");
    await driver.sleep(5000);
    assert(
      (await driver.findElement(By.id("testing profile name")).getText()) ==
        "Leon Kennedy"
    );
    await driver.findElement(By.css(".edit-button")).click();
    await driver.findElement(By.id(":r3:")).click();
    await driver.findElement(By.id(":r3:")).clear();
    await driver.findElement(By.id(":r3:")).sendKeys("Michael");
    await driver
      .findElement(By.css(".css-uln3b4-MuiButtonBase-root-MuiButton-root"))
      .click();
    await driver.sleep(2000);
    await driver.get("http://localhost:5173/components/Profile");
    await driver.sleep(2000);
    assert(
      (await driver.findElement(By.css(".profile-name")).getText()) ==
        "Michael Kennedy"
    );
    await driver.findElement(By.css(".edit-button")).click();
    await driver.findElement(By.id(":r3:")).click();
    await driver.findElement(By.id(":r3:")).clear();
    await driver.findElement(By.id(":r3:")).sendKeys("Leon");
    await driver
      .findElement(By.css(".css-uln3b4-MuiButtonBase-root-MuiButton-root"))
      .click();
    await driver.sleep(2000);
  });

  it("Edit Firstname to no name", async function () {
    await signIn();
    await driver.get("http://localhost:5173/components/Profile");
    await driver.sleep(5000);
    assert(
      (await driver.findElement(By.id("testing profile name")).getText()) ==
        "Leon Kennedy"
    );
    await driver.findElement(By.css(".edit-button")).click();
    await driver.findElement(By.id(":r3:")).click();
    await driver.findElement(By.id(":r3:")).clear();
    await driver
      .findElement(By.css(".css-uln3b4-MuiButtonBase-root-MuiButton-root"))
      .click();
    await driver.sleep(2000);
    await driver.get("http://localhost:5173/components/Profile");
    await driver.sleep(2000);
    assert(
      (await driver.findElement(By.css(".profile-name")).getText()) ==
        "Leon Kennedy"
    );
  });
});
